$(document).ready(function(){var s=$("#contact-form"),t=$("#form-messages");$(s).submit(function(e){e.preventDefault();var a=$(s).serialize();$.ajax({type:"POST",url:$(s).attr("action"),data:a}).done(function(e){$(t).removeClass("error"),$(t).addClass("success"),$(t).text(e),$("#name").val(""),$("#email").val(""),$("#phone").val(""),$("#message").val("")}).fail(function(e){$(t).removeClass("success"),$(t).addClass("error"),""!==e.responseText?$(t).text(e.responseText):$(t).text("oops! Something has gone wrong and your message wasnt sent.")})})});